export function createStore(rootReducer) {
    const state = {}
    const subscribers = []

    return{
        dispatch(action) {
             state = rootReducer(state, action)
        },
        subscribe(callback) {
            subscribers.push(callback)
        }, 
        getState() {
            return state
        }
    }
}
