import React from 'react'

const PostFilter = ({filter, setFilter}) => {
    return(
        <div>
        <Inp 
        value={filter.query}
        onChange={e => setSearchQuery(e.target.value)}
        placeholder='Поиск...'
        />
        <Slc
        value={filter.sort}
        onChange={sortPosts}
        defaultValue="Сортировка"
        options={[
          {value: 'title', name: 'По названию'},
          {value: 'body', name: 'По описанию'}
        ]}
        />
      </div>
    )
}

export default PostFilter
