import React, { useMemo, useState, useRef } from "react";
import ClassCounter from "./components/ClassCounter";
import Counter from "./components/Counter";
import PostFilter from "./components/PostFilter";
import PostForm from "./components/PostForm";
import PostItem from "./components/PostItem";
import PostList from "./components/PostList";
import Btn from "./components/UI/button/Btn"
import Inp from "./components/UI/input/Inp";
import Modal from "./components/UI/modal/Modal";
import Slc from "./components/UI/select/Slc";
import './styles/App.css';

function App() {
  const [posts, setPosts] = useState([
    {id: 1, title: 'JavaScript', body: 'C discription'},
    {id: 2, title: 'Python', body: 'A discription'},
    {id: 3, title: 'C++', body: 'B discription'}
  ])

  const [filter, setFilter] = useState({sort: '', query: ''})


  const sortedPosts = useMemo(() => {

    if(filter.sort) {
      return [...posts].sort((a, b) => a[filter.sort].localeCompare(b[filter.sort]))
    }
    return posts

  }, [filter.sort, posts])

  const sortedAndSerchedPosts = useMemo(() => {
    return sortedPosts.filter(post => post.title.toLowerCase().includes(filter.query.toLowerCase()))
  }, [filter.query, sortedPosts])

  const createPost = (newPost) => {
    setPosts([...posts, newPost])
  }

  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))
  }

  return (
    <div className="App">
      <Modal visible={true}>
        <PostForm create={createPost}/>
      </Modal>
      <hr style={{margin: '15px 0'}}/>
      <PostFilter filter={filter} setFilter={setFilter}/>
      <PostList remove={removePost} posts={sortedAndSerchedPosts} title="JS посты"/>

    </div>
  );
}

export default App;
