        <Inp 
        value={post.title}
        onChange={e => setPost({...post, title: e.target.value})}
        type="text" 
        placeholder="Название поста"
        />

        <Inp 
        value={post.body}
        onChange={e => setPost({...post, body: e.target.value})}
        type="text" 
        placeholder="Описание поста"
        /> 

        <Btn onClick={addNewPost}>Создать</Btn>
      </form>
    )
}

